<?phpnamespace Nemundo\Admin\Com\Card;use Nemundo\Html\Block\Div;use Nemundo\Html\Container\AbstractContainer;use Nemundo\Html\Container\AbstractHtmlContainer;use Nemundo\Html\Formatting\Bold;class AdminCard extends Div{    /**     * @var string     */    public $cardTitle;    // cardTitle    /**     * @var Div     */    protected $headerDiv;    /**     * @var Div     */    protected $contentDiv;    protected function loadContainer()    {        $this->addCssClass('admin-card');        //$this->addCssClass('mb-3');        $this->headerDiv = new Div();        $this->headerDiv->addCssClass('admin-card-title');        parent::addContainer($this->headerDiv);        $this->contentDiv = new Div();        $this->contentDiv->addCssClass('admin-card-content');        parent::addContainer($this->contentDiv);    }    public function getContent()    {        if ($this->cardTitle !== null) {            $bold = new Bold($this->headerDiv);            $bold->content = $this->cardTitle;        }        return parent::getContent();    }    public function addContainer(AbstractContainer $container)    {        $this->contentDiv->addContainer($container);    }}