<?phpnamespace Nemundo\Admin\Com\ListBox;use Nemundo\Com\FormBuilder\Item\AbstractFileUpload;use Nemundo\Html\Form\Formatting\Label;use Nemundo\Html\Formatting\Bold;class AdminFileUpload extends AbstractFileUpload{    use AdminErrorMessageTrait;    public function getContent()    {        $this->prepareHtml();        $this->tagName = 'div';        $this->addCssClass('admin-textbox');        $label = new Label();        $label->content = $this->getLabelErrorMessage();        /*        if ($this->showErrorMessage) {            $bold = new Bold();            $bold->content = $this->errorMessage;            $label->content .= ' ' . $bold->getBodyContent();        }*/        $this->addContainer($label);        $this->addContainer($this->fileInput);        return parent::getContent();    }}