<?phpnamespace Nemundo\App\Application\Com\Form;use Nemundo\Package\Bootstrap\Form\BootstrapForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapFileUpload;use Nemundo\Project\Path\TmpPath;class TmpUploadForm extends BootstrapForm{    /**     * @var BootstrapFileUpload     */    private $file;    public function getContent()    {        $this->file=new BootstrapFileUpload($this);        return parent::getContent();    }    protected function onSubmit()    {        $this->file->getFileRequest()->saveAsOrginalFilename((new TmpPath())->getPath());        exit;    }}