<?phpnamespace Nemundo\App\Git\Command;use Nemundo\Core\Local\AbstractLocalCommand;use Nemundo\Core\Type\Text\Text;class GitCommit extends AbstractLocalCommand{    public $path;    protected function loadCommand()    {    }    public function runCommand()    {        $drive = (new Text($this->path))->getSubstring(0, 2);        $this->addCommand($drive);        $this->addCommand('cd ' . $this->path);        $this->addCommand('git add *');        $this->addCommand('git update-index --chmod=+x deploy');        $this->addCommand('git update-index --chmod=+x install');        $this->addCommand('git commit -m "."');        //$this->addCommand('git push');        return parent::runCommand();    }}