<?phpnamespace Nemundo\App\Linux\Cmd;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Core\Debug\Debug;abstract class AbstractLocalCmd extends AbstractCmd{    /**     * @var bool     */    protected $showOutput = false;    private $commandLineList = [];    //abstract protected function loadCommand();    protected function loadCmd()    {        // TODO: Implement loadCmd() method.    }    protected function addCommandLine($command)    {        $this->commandLineList[] = $command;        return $this;    }    protected function getCommand() {    }    protected function runCommand()    {        //$this->loadCommand();        $newCommand = join(' && ', $this->commandLineList);        exec($newCommand, $output);        //(new Debug())->write($newCommand);        if ($this->showOutput) {            (new Debug())->write($output);        }        return $output;    }}