<?phpnamespace Nemundo\App\Linux\Cmd;class TextFileCreatorCmd extends AbstractCmd{    public $filename;    public $content;    protected function loadCmd()    {        // TODO: Implement loadCmd() method.    }    public function createFile() {       $this->addCommandLine('echo "'.$this->content.'" | sudo tee '.$this->filename.';');    }    public function getCmd()    {        $this->createFile();        return parent::getCmd(); // TODO: Change the autogenerated stub    }}