<?phpnamespace Nemundo\App\ModelDesigner\Com\Form;use Nemundo\App\ModelDesigner\Com\ListBox\ProjectListBox;use Nemundo\App\ModelDesigner\ModelDesignerConfig;use Nemundo\App\ModelDesigner\Project\DefaultProject;use Nemundo\Core\Json\Document\JsonDocument;use Nemundo\Package\Bootstrap\Form\BootstrapForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class DefaultProjectForm extends BootstrapForm{    /**     * @var ProjectListBox     */    private $project;    public function getContent()    {        $this->project = new BootstrapListBox($this);        $this->project->label = 'Default Project';        //$this->project->submitOnChange = true;        foreach ((new ModelDesignerConfig())->getProjectCollection()->getProjectList() as $project) {            $this->project->addItem($project->getClassName(), $project->project . ' (' . $project->namespace . ')');        }        $project = (new DefaultProject())->getDefaultProject();        if ($project !== null) {            $this->project->value = $project->getClassName();        }        return parent::getContent();    }    protected function onSubmit()    {        $config = [];        $config['project'] = $this->project->getValue();        $file = new JsonDocument();        $file->filename = (new DefaultProject())->getConfigFilename();        $file->addRow($config);        $file->writeFile();    }}