<?phpnamespace Nemundo\App\System\Com\Table;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;class ApacheModuleTable extends AdminTable{    public function getContent()    {        //print_r(apache_get_modules());        $header=new AdminTableHeader($this);        $header->addText('Module');        foreach (apache_get_modules() as $module) {            $row=new AdminTableRow($this);            $row->addText($module);        }        return parent::getContent(); // TODO: Change the autogenerated stub    }}