<?phpnamespace Nemundo\Bfs\Abstimmung\Com\ListBox;use Nemundo\Bfs\Abstimmung\Data\Datum\DatumReader;use Nemundo\Bfs\Abstimmung\Parameter\DatumParameter;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class DatumListBox extends BootstrapListBox{    /**     * @var bool     */    public $filterByJahr = false;    public $jahr;    protected function loadContainer()    {        parent::loadContainer(); // TODO: Change the autogenerated stub        $this->name=(new DatumParameter())->getParameterName();    }    public function getContent()    {        $this->label = 'Datum';        $reader = new DatumReader();        if ($this->filterByJahr) {            $reader->filter->andEqual($reader->model->jahr,$this->jahr);        }        foreach ($reader->getData() as $datumRow) {            $this->addItem($datumRow->id,$datumRow->datum->getShortDateLeadingZeroFormat());        }        return parent::getContent();    }}