<?phpnamespace Nemundo\Bfs\Abstimmung\Com\ListBox;use Nemundo\Bfs\Abstimmung\Data\GeoLevel\GeoLevelReader;use Nemundo\Bfs\Abstimmung\Parameter\GeoLevelParameter;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class GeoLevelListBox extends BootstrapListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->name = (new GeoLevelParameter())->getParameterName();    }    public function getContent()    {        $this->label = 'Geo Level';        $reader = new GeoLevelReader();        $reader->addOrder($reader->model->id);        foreach ($reader->getData() as $geoLevelRow) {            $this->addItem($geoLevelRow->id, $geoLevelRow->geoLevel);        }        return parent::getContent();    }}