<?phpnamespace Nemundo\Bfs\Abstimmung\Com\ListBox;use Nemundo\Bfs\Abstimmung\Data\Geo\GeoReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class GeoListBox extends BootstrapListBox{    public $geoLevelId;    public function getContent()    {        $this->label = 'Geo';        if ($this->geoLevelId !== null) {            $reader = new GeoReader();            $reader->filter->andEqual($reader->model->geoLevelId,$this->geoLevelId);            $reader->addOrder($reader->model->geo);            foreach ($reader->getData() as $geoRow) {                $this->addItem($geoRow->id, $geoRow->geo);            }        }        return parent::getContent();    }}