<?phpnamespace Nemundo\Bfs\Abstimmung\Com\ListBox;use Nemundo\Bfs\Abstimmung\Data\Jahr\JahrReader;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class JahrListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Jahr';        $reader= new JahrReader();        $reader->addOrder($reader->model->id, SortOrder::DESCENDING);        foreach ($reader->getData() as $jahrRow) {            $this->addItem($jahrRow->id,$jahrRow->id);        }        return parent::getContent();    }}