<?phpnamespace Nemundo\Bfs\Abstimmung\Com\ListBox;use Nemundo\Bfs\Abstimmung\Data\ListSortOrder\ListSortOrderReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class SortOrderListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Sort Order';        $reader=new ListSortOrderReader();        $reader->addOrder($reader->model->id);        foreach ($reader->getData() as $sortOrderRow) {            $this->addItem($sortOrderRow->id,$sortOrderRow->sortOrder);        }        return parent::getContent();    }}