<?phpnamespace Nemundo\Bfs\Abstimmung\Content\Abstimmung;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\Bfs\Abstimmung\Data\Resultat\ResultatReader;use Nemundo\Bfs\Abstimmung\Type\GeoLevel\BundGeoLevel;use Nemundo\Content\View\AbstractContentView;class AbstimmungContentView extends AbstractContentView{    /**     * @var AbstimmungContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'Schweiz';        $this->viewId = 'a9e212aa-d347-431a-bc12-825418ebb6b6';        $this->defaultView = true;    }    public function getContent()    {        $reader = new ResultatReader();        $reader->model->loadAbstimmung();        $reader->model->abstimmung->loadDatum();        $reader->filter->andEqual($reader->model->abstimmungId, $this->contentType->getDataId());        //$reader->filter->andEqual($reader->model->geoId, (new LandGeoLevel())->id);        $reader->filter->andEqual($reader->model->geoLevelId, (new BundGeoLevel())->id);        foreach ($reader->getData() as $resultatCustomRow) {            $table = new AdminLabelValueTable($this);            $table->addLabelValue($resultatCustomRow->model->abstimmung->datum->label, $resultatCustomRow->abstimmung->datum->datum->getLongFormat());            $table->addLabelValue($resultatCustomRow->model->jaProzent->label, $resultatCustomRow->getJaProzent());            $table->addLabelValue($resultatCustomRow->model->jaAbsolut->label, $resultatCustomRow->getJaStimmen());            $table->addLabelValue($resultatCustomRow->model->neinAbsolut->label, $resultatCustomRow->getNeinStimmen());            $table->addLabelValue($resultatCustomRow->model->stimmbeteiligungProzent->label, $resultatCustomRow->getStimmbeteiligungProzent());            $table->addLabelValue($resultatCustomRow->model->neinAbsolut->label, $resultatCustomRow->getStimmbeteiligungProzent());        }        return parent::getContent();    }}