<?phpnamespace Nemundo\Bfs\Abstimmung\Content\AbstimmungResultatWidget;use Nemundo\Bfs\Abstimmung\Com\ListBox\AbstimmungListBox;use Nemundo\Bfs\Abstimmung\Com\ListBox\GeoLevelListBox;use Nemundo\Bfs\Abstimmung\Com\ListBox\SortOrderListBox;use Nemundo\Bfs\Abstimmung\Data\AbstimmungResultatWidget\AbstimmungResultatWidgetModel;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;class AbstimmungResultatWidgetContentForm extends AbstractContentForm{    /**     * @var AbstimmungResultatWidgetContentType     */    public $contentType;    /**     * @var AbstimmungListBox     */    private $abstimmung;    /**     * @var GeoLevelListBox     */    private $geoLevel;    /**     * @var BootstrapTextBox     */    private $limit;    /**     * @var SortOrderListBox     */    private $sortOrder;    public function getContent()    {        $model = new AbstimmungResultatWidgetModel();        $this->abstimmung = new AbstimmungListBox($this);        $this->abstimmung->validation = true;        $this->geoLevel = new GeoLevelListBox($this);        $this->geoLevel->validation = true;        $this->limit = new BootstrapTextBox($this);        $this->limit->label = $model->limit->label;        $this->limit->validation = true;        $this->limit->value = 10;        $this->sortOrder = new SortOrderListBox($this);        $this->sortOrder->emptyValueAsDefault = false;        return parent::getContent();    }    protected function loadUpdateForm()    {        $row = $this->contentType->getDataRow();        $this->abstimmung->value = $row->abstimmungId;        $this->geoLevel->value = $row->geoLevelId;        $this->limit->value = $row->limit;        $this->sortOrder->value = $row->sortOrderId;    }    public function onSubmit()    {        $this->contentType->abstimmungId = $this->abstimmung->getValue();        $this->contentType->geoLevelId = $this->geoLevel->getValue();        $this->contentType->limit = $this->limit->getValue();        $this->contentType->sortOrderId = $this->sortOrder->getValue();        $this->contentType->saveType();    }}