<?phpnamespace Nemundo\Bfs\Abstimmung\Content\AbstimmungResultatWidget;use Nemundo\Bfs\Abstimmung\Com\Table\GeoAbstimmungTable;use Nemundo\Content\View\AbstractContentView;class AbstimmungResultatWidgetContentView extends AbstractContentView{    /**     * @var AbstimmungResultatWidgetContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = 'cb468dea-c54d-496e-a20d-f7638dbfea57';        $this->defaultView = true;    }    public function getContent()    {        $abstimmungResultatRow = $this->contentType->getDataRow();        /*        $h1=new H1($this);        $h1->content=$abstimmungResultatRow->abstimmung->abstimmung;*/        /*   $p = new Paragraph($this);           $p->content = $abstimmungResultatRow->abstimmung->datum->datum->getLongFormat();*/        $table = new GeoAbstimmungTable($this);        $table->abstimmungId = $abstimmungResultatRow->abstimmungId;        $table->geoLevelRow = $abstimmungResultatRow->geoLevel;  // geoLevelId;        //$table->geoLevelId=$abstimmungResultatRow->geoLevelId;        $table->resultatReader->limit = $abstimmungResultatRow->limit;        $table->resultatReader->addOrder($table->resultatReader->model->jaProzent, $abstimmungResultatRow->sortOrder->sql);        return parent::getContent();    }}