<?phpnamespace Nemundo\Bfs\Abstimmung\Import;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Json\Reader\JsonReader;use Nemundo\Core\Type\DateTime\Date;class DatumImport extends AbstractBase{    /**     * @var bool     */    public $showProgress = false;    public function importDatum()    {        $url = 'https://ckan.opendata.swiss/api/3/action/package_show?id=echtzeitdaten-am-abstimmungstag-zu-eidgenoessischen-abstimmungsvorlagen';        $reader = new JsonReader();        $reader->fromUrl($url);        $json = $reader->getData();        foreach ($json['result']['resources'] as $resourceJson) {            if ($this->showProgress) {                $date = new Date($resourceJson['coverage']);                (new Debug())->write($date->getShortDateLeadingZeroFormat());            }            $downloadUrl = $resourceJson['download_url'];            $import = new AbstimmungImport();            $import->showProgress = $this->showProgress;            $import->importAbstimmung($downloadUrl);        }    }}