<?phpnamespace Nemundo\Bfs\Abstimmung\Page;use Nemundo\Bfs\Abstimmung\Com\ListBox\AbstimmungListBox;use Nemundo\Bfs\Abstimmung\Com\ListBox\GeoListBox;use Nemundo\Bfs\Abstimmung\Com\Table\GeoAbstimmungBootstrapTable;use Nemundo\Bfs\Abstimmung\Type\GeoLevel\GemeindeGeoLevel;use Nemundo\Bfs\Abstimmung\Type\GeoLevel\KantonGeoLevel;use Nemundo\Com\FormBuilder\SearchForm;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;class _AbstimmungPage extends AbstractTemplateDocument{    public function getContent()    {        $form = new SearchForm($this);        $row = new BootstrapRow($form);        $abstimmung = new AbstimmungListBox($row);        $abstimmung->submitOnChange = true;        $abstimmung->searchMode = true;        $abstimmung->column = true;        $abstimmung->columnSize = 2;        if ($abstimmung->hasValue()) {            $table2 = new GeoAbstimmungBootstrapTable($this);//            $table2->geoLevelId = (new KantonGeoLevel())->level;            $table2->geoLevelId = (new GemeindeGeoLevel())->id;            $table2->abstimmungId=$abstimmung->getValue();            //$table2->resultatReader->filter->andEqual($table2->resultatReader->model->abstimmungId, $abstimmung->getValue());        }        /*   if ($geo->hasValue()) {               $table2->resultatReader->filter->andEqual($table2->resultatReader->model->geoId, $geo->getValue());           }*/        return parent::getContent();    }}