<?phpnamespace Nemundo\Bfs\Abstimmung\Row;use Nemundo\Bfs\Abstimmung\Data\Resultat\ResultatRow;use Nemundo\Core\Type\Number\Number;class ResultatCustomRow extends ResultatRow{    public function getJaStimmen()    {        $text = (new Number($this->jaAbsolut))->getFormatNumber();        return $text;    }    public function getNeinStimmen()    {        $text = (new Number($this->neinAbsolut))->getFormatNumber();        return $text;    }    public function getJaProzent()    {        return $this->getProzent($this->jaProzent);    }    public function getStimmbeteiligungProzent()    {        //$number=(new Number($this->jaProzent))->roundNumber(1).' %';        return $this->getProzent($this->stimmbeteiligungProzent);  //JaPercent() $number;    }    private function getProzent($number)    {        $number = (new Number($number))->getRoundedNumber(1) . ' %';        return $number;    }}