<?phpnamespace Nemundo\Bfs\Abstimmung\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Bfs\Abstimmung\Data\Resultat\ResultatDelete;use Nemundo\Bfs\Abstimmung\Import\DatumImport;use Nemundo\Bfs\Gemeinde\Import\GemeindeImport;class AbstimmungImportScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'abstimmung-import';    }    public function run()    {        (new GemeindeImport())->import();        (new ResultatDelete())->delete();        $import = new DatumImport();        $import->showProgress = true;        $import->importDatum();    }}