<?phpnamespace Nemundo\Com\Video\YouTube;use Nemundo\Com\Video\AbstractVideoPlayer;use Nemundo\Core\Http\Url\UrlBuilder;use Nemundo\Core\Http\Url\UrlInformation;use Nemundo\Html\Iframe\Iframe;// https://developers.google.com/youtube/iframe_api_reference// Packageclass YouTubePlayer extends AbstractVideoPlayer{    public function getContent()    {        $this->checkProperty('videoId');        $url = 'https://www.youtube.com/embed/' . $this->videoId;        $iframe = new Iframe($this);        $iframe->width = $this->width;        $iframe->height = $this->height;        $iframe->src = $url;        $iframe->addAttribute('allow', 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture');        $iframe->addAttributeWithoutValue('allowfullscreen');        return parent::getContent();    }}