<?phpnamespace Nemundo\Content\Action;// ActionTraittrait ContentActionTrait{    /**     * @var AbstractAction[]     */    private $contentActionList = [];    // addAction    public function addContentAction(AbstractAction $contentAction)    {        $this->contentActionList[] = $contentAction;        return $this;    }    public function getContentActionList()    {        return $this->contentActionList;    }}