<?phpnamespace Nemundo\Content\Action\Parameter;use Nemundo\Content\Action\AbstractAction;use Nemundo\Content\Data\Content\ContentReader;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Web\Parameter\AbstractUrlParameter;class ActionParameter extends AbstractUrlParameter   // ContentTypeParameter  // AbstractContentTypeUrlParameter{    protected function loadParameter()    {        $this->parameterName = 'action';    }    public function getAction() {        $content = (new ContentParameter())->getContent(false);        $actionRow=(new ContentActionReader())->getRowById($this->getValue());        /** @var AbstractAction $action */        //$action = new $actionRow->phpClass($content);        return $actionRow->getAction($content);    }}