<?phpnamespace Nemundo\Content\App\Favorite\Site;use Nemundo\Content\App\Favorite\Action\FavoriteAction;use Nemundo\Package\FontAwesome\Site\AbstractIconSite;use Nemundo\Content\App\Favorite\Data\Favorite\Favorite;use Nemundo\Content\App\Favorite\Icon\EmptyFavoriteIcon;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\User\Session\UserSession;class FavoriteSaveSite extends AbstractIconSite{    /**     * @var FavoriteSaveSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Favorite Save';        $this->url = 'favorite-save';        $this->menuActive = false;        $this->icon->icon = 'star-half-stroke';   //= new EmptyFavoriteIcon();        //<i class="fa-light fa-star-half-stroke"></i>        new FavoriteDeleteSite($this);        FavoriteSaveSite::$site = $this;    }    public function loadContent()    {        $contentParameter = new ContentParameter();        $contentParameter->contentTypeCheck = false;        $action=new FavoriteAction();        $action->onAction($contentParameter->getContent());        (new UrlReferer())->redirect();    }}