<?phpnamespace Nemundo\Content\App\Stream\Install;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\Content\App\Stream\Action\StreamAction;use Nemundo\Content\App\Stream\Action\StreamDeleteAction;use Nemundo\Content\App\Stream\Content\StreamWidget\StreamWidgetContentType;use Nemundo\Content\App\Stream\Data\StreamModelCollection;use Nemundo\Content\App\Stream\Usergroup\StreamUsergroup;use Nemundo\Content\Setup\ContentActionSetup;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\User\Setup\UsergroupSetup;class StreamInstall extends AbstractInstall{    public function install()    {        (new ModelCollectionSetup())            ->addCollection(new StreamModelCollection());        (new ContentActionSetup())            ->addContentAction(new StreamDeleteAction())            ->addContentAction(new StreamAction());        (new ContentTypeSetup())            ->addContentType(new StreamWidgetContentType());        /*(new ContentTypeCollectionSetup())            ->addContentTypeCollection(new StreamContentTypeCollection());*/        (new UsergroupSetup())            ->addUsergroup(new StreamUsergroup());    }}