<?phpnamespace Nemundo\Content\App\Text\Service;use Nemundo\App\WebService\Service\AbstractServiceRequest;use Nemundo\Content\App\Text\Content\LargeText\LargeTextContentType;use Nemundo\Content\App\Text\Content\Text\TextContentType;use Nemundo\Content\Service\AbstractContentServiceRequest;use Nemundo\Core\Http\Request\HttpRequest;class LargeTextService extends AbstractContentServiceRequest{    protected function loadService()    {        $this->serviceName = 'large-text-post';    }    public function onRequest()    {        $id = null;        $request = new HttpRequest('id');        if ($request->hasValue()) {            $id=$request->getValue();        }        $contentType = new LargeTextContentType($id);        $contentType->largeText = (new HttpRequest('text'))->getValue();        $contentType->saveType();        //$this->saveTree($contentType);        $this->sendContentOkStatus($contentType);    }}