<?phpnamespace Nemundo\Content\Index\Search\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\Index\Search\Action\SearchIndexAction;use Nemundo\Content\Index\Search\Application\SearchApplication;use Nemundo\Content\Index\Search\Data\SearchModelCollection;use Nemundo\Content\Index\Search\Script\SearchCleanScript;use Nemundo\Content\Index\Search\Script\SearchIndexRepairScript;use Nemundo\Content\Index\Search\Script\WordCleanScript;use Nemundo\Content\Index\Search\Service\ContentSearchService;use Nemundo\Content\Index\Search\Service\WordService;use Nemundo\Model\Setup\ModelCollectionSetup;class SearchIndexInstall extends AbstractInstall{    public function install()    {        (new ApplicationSetup())            ->addApplication(new SearchApplication());        (new ModelCollectionSetup())            ->addCollection(new SearchModelCollection());        (new ScriptSetup(new SearchApplication()))            ->addScript(new SearchCleanScript())            ->addScript(new SearchIndexRepairScript())            ->addScript(new WordCleanScript());        //(new IndexTypeSetup())->addIndexType(new SearchIndexType());        (new ActionSetup())->addContentAction(new SearchIndexAction());        (new ServiceRequestSetup(new SearchApplication()))            ->addService(new WordService())            ->addService(new ContentSearchService());       /* (new WebServiceSetup())            ->addWebService(new WordService())            ->addWebService(new ContentSearchWebService());*/    }}