<?phpnamespace Nemundo\Content\Page\Admin;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Template\ContentAdminTemplate;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class ActionAdminPage extends ContentAdminTemplate{    public function getContent()    {        $layout = new BootstrapTwoColumnLayout($this);        $actionReader = new ContentActionReader();        $actionReader->addOrder($actionReader->model->action);        $table = new AdminClickableTable($layout->col1);        $header = new TableHeader($table);        $header->addText($actionReader->model->action->label);        $header->addText($actionReader->model->id->label);        foreach ($actionReader->getData() as $contentActionRow) {            $row = new BootstrapClickableTableRow($table);            $row->addText($contentActionRow->action);            $row->addText($contentActionRow->id);        }        return parent::getContent();    }}