<?phpnamespace Nemundo\Content\Page;use Muenster\App\Verortung\Action\VerortungAction;use Muenster\App\Verortung\Action\VerortungActionView;use Muenster\App\Verortung\Action\VerortungActionForm;use Muenster\App\Verortung\Index\VerortungIndexForm;use Nemundo\Admin\Com\Copy\CopyTextBox;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Widget\AdminWidget;use Nemundo\Admin\Parameter\PageParameter;use Nemundo\App\Application\Com\ListBox\ApplicationListBox;use Nemundo\Com\FormBuilder\SearchForm;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Content\Action\Com\ActionWidget;use Nemundo\Content\Action\ViewAction;use Nemundo\Content\App\ContentPrint\Action\ContentPrintAction;use Nemundo\Content\App\PublicShare\Action\PublicShareAction;use Nemundo\Content\Com\ListBox\ContentTypeListBox;use Nemundo\Content\Com\ListBox\ViewContentTypeListBox;use Nemundo\Content\Com\Widget\ContentWidget;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Index\Tree\Com\Container\ContentChildContainer;use Nemundo\Content\Index\Tree\Com\Container\TreeIndexContainer;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Content\Site\ContentSite;use Nemundo\Content\Site\Json\ContentViewJsonSite;use Nemundo\Content\Template\ContentTemplate;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class ContentPage extends ContentTemplate{    public function getContent()    {        $contentTypeParameter = new ContentTypeParameter();        if ($contentTypeParameter->hasValue()) {            $contentType = $contentTypeParameter->getContentType();            $layout = new BootstrapTwoColumnLayout($this);            if ($contentType->hasList()) {                $contentTypeListBox = $contentType->getListing($layout->col1);                $contentTypeListBox->redirectSite = clone(ContentSite::$site);                $contentTypeListBox->redirectSite->addParameter(new ContentTypeParameter());                $contentTypeListBox->redirectSite->addParameter(new PageParameter());            }            $contentParameter = new ContentParameter();            if ($contentParameter->hasValue()) {                $content = $contentParameter->getContent(false);                if ($content->hasView()) {                    $widget = new ContentWidget($layout->col2);                    $widget->contentType = $content;                    $widget->showAction=false;                    $widget->loadAction = false;                    $widget->redirectSite = ContentSite::$site;                    $jsonSite = clone(ContentViewJsonSite::$site);                    $jsonSite->addParameter(new ContentParameter());                    $txt=new CopyTextBox($layout->col2);                    $txt->value=$jsonSite->getUrlWithDomain();                }            }        }        return parent::getContent();    }}