<?phpnamespace Nemundo\Content\Row;use Nemundo\Content\Action\AbstractAction;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Data\ContentAction\ContentActionRow;class ActionCustomRow extends ContentActionRow{    public function getAction()    {        $actionRow = (new ContentActionReader())->getRowById($this->id);        $action = null;        if (class_exists($actionRow->phpClass)) {            /** @var AbstractAction $action */            $action = new $actionRow->phpClass();        }        return $action;    }}