<?phpnamespace Nemundo\Core\Command;use Nemundo\Core\Base\AbstractBase;abstract class AbstractCommand extends AbstractBase{    public $label;    public $command;    //public $description;    protected function loadCmd() {    }    public function __construct() {        $this->loadCmd();    }    private $commandLineList = [];    protected function addCommandLine($command)    {        $this->commandLineList[] = $command;        return $this;    }    public function addCommand(AbstractCommand $cmd) {        foreach ($cmd->getCmd() as $line) {            $this->addCommandLine($line);        }        return $this;    }    public function getCmd() {        return $this->commandLineList;    }    public function getCmdText() {        $text = join(PHP_EOL, $this->getCmd());        return $text;    }}