<?phpnamespace Nemundo\Core\Command;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Core\Debug\Debug;abstract class AbstractLocalCommand extends AbstractBaseClass{    /**     * @var AbstractCommand     */    protected $command;    public function __construct(AbstractCommand $command) {        $this->command=$command;    }    protected function runCommand()    {        $commandList=[];        foreach ($this->command->getCmd() as $cmd) {            $commandList[]= $cmd;        }        $newCommand = join(' && ', $commandList);        exec($newCommand, $output);        //(new Debug())->write($newCommand);      /*  if ($this->showOutput) {            (new Debug())->write($output);        }*/        return $output;    }}