<?phpnamespace Nemundo\Meteo\AviationWeather\Data\Metar;class MetarPaginationReader extends \Nemundo\Model\Reader\AbstractPaginationModelDataReader {/*** @var MetarModel*/public $model;public function __construct() {parent::__construct();$this->model = new MetarModel();}/*** @return MetarRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = new MetarRow($dataRow, $this->model);$list[] = $row;}return $list;}}