<?phpnamespace Nemundo\Meteo\AviationWeather\Data\Metar;class MetarReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var MetarModel*/public $model;public function __construct() {parent::__construct();$this->model = new MetarModel();}/*** @return MetarRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return MetarRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return MetarRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new MetarRow($dataRow, $this->model);$row->model = $this->model;return $row;}}