<?phpnamespace Nemundo\Meteo\AviationWeather\Data\Metar;use Nemundo\Model\Data\AbstractModelUpdate;class MetarUpdate extends AbstractModelUpdate {/*** @var MetarModel*/public $model;/*** @var string*/public $stationId;/*** @var bool*/public $hasQnh;/*** @var float*/public $qnh;/*** @var bool*/public $hasTemperature;/*** @var float*/public $temperature;/*** @var string*/public $metarRaw;public function __construct() {parent::__construct();$this->model = new MetarModel();}public function update() {$this->typeValueList->setModelValue($this->model->stationId, $this->stationId);$this->typeValueList->setModelValue($this->model->hasQnh, $this->hasQnh);if (!is_null($this->qnh)) $this->qnh = str_replace(",", ".", $this->qnh);$this->typeValueList->setModelValue($this->model->qnh, $this->qnh);$this->typeValueList->setModelValue($this->model->hasTemperature, $this->hasTemperature);if (!is_null($this->temperature)) $this->temperature = str_replace(",", ".", $this->temperature);$this->typeValueList->setModelValue($this->model->temperature, $this->temperature);$this->typeValueList->setModelValue($this->model->metarRaw, $this->metarRaw);parent::update();}}