<?phpnamespace Nemundo\Meteo\AviationWeather\Data\MetarStation;class MetarStationReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var MetarStationModel*/public $model;public function __construct() {parent::__construct();$this->model = new MetarStationModel();}/*** @return MetarStationRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return MetarStationRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return MetarStationRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new MetarStationRow($dataRow, $this->model);$row->model = $this->model;return $row;}}