<?phpnamespace Nemundo\Meteo\AviationWeather\Data\MetarStation;use Nemundo\Model\Data\AbstractModelUpdate;class MetarStationUpdate extends AbstractModelUpdate {/*** @var MetarStationModel*/public $model;/*** @var string*/public $stationCode;/*** @var \Nemundo\Core\Type\Geo\GeoCoordinate*/public $coordinate;public function __construct() {parent::__construct();$this->model = new MetarStationModel();$this->coordinate = new \Nemundo\Core\Type\Geo\GeoCoordinate();}public function update() {$this->typeValueList->setModelValue($this->model->stationCode, $this->stationCode);$property = new \Nemundo\Model\Data\Property\Geo\GeoCoordinateDataProperty($this->model->coordinate, $this->typeValueList);$property->setValue($this->coordinate);parent::update();}}