<?phpnamespace Nemundo\Meteo\AviationWeather\DataList;use Nemundo\Core\DataList\AbstractDataList;use Nemundo\Core\Type\Geo\AbstractGeoCoordinate;use Nemundo\Meteo\AviationWeather\Data\MetarStation\MetarStation;use Nemundo\Meteo\AviationWeather\Data\MetarStation\MetarStationReader;class MetarStationDataList extends AbstractDataList{    /**     * @var AbstractGeoCoordinate     */    public $coordinate;    protected function loadDataList()    {        // TODO: Implement loadDataList() method.        $reader=new MetarStationReader();        foreach ($reader->getData() as $stationRow) {            $this->addIdValue($stationRow->id,$stationRow->stationCode);        }    }    // AbstractDataItem    // ($valueItem)    protected function onNew($value)    {        $data=new MetarStation();        $data->stationCode=$value;        $data->coordinate=$this->coordinate;        $dataId=$data->save();        $this->addIdValue($dataId,$value);        //exit;        // TODO: Implement onNew() method.    }}