<?phpnamespace Nemundo\Meteo\AviationWeather\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Scheduler\Setup\SchedulerSetup;use Nemundo\Meteo\AviationWeather\Application\AviationWeatherApplication;use Nemundo\Meteo\AviationWeather\Data\AviationWeatherModelCollection;use Nemundo\Meteo\AviationWeather\Scheduler\MetarImportScheduler;use Nemundo\Model\Setup\ModelCollectionSetup;class AviationWeatherInstall extends AbstractInstall{    public function install()    {        (new ApplicationSetup())->addApplication(new AviationWeatherApplication());        (new ModelCollectionSetup())->addCollection(new AviationWeatherModelCollection());        (new SchedulerSetup(new AviationWeatherApplication()))            ->addScheduler(new MetarImportScheduler());    }}