<?phpnamespace Nemundo\Meteo\AviationWeather\Site;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Geo\Kml\Document\KmlDocument;use Nemundo\Geo\Kml\Object\KmlMarker;use Nemundo\Geo\Kml\Site\AbstractKmlSite;use Nemundo\Html\Block\Div;use Nemundo\Html\Heading\H2;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Html\Table\Table;use Nemundo\Meteo\AviationWeather\Data\Metar\MetarReader;use Nemundo\Meteo\AviationWeather\Data\MetarStation\MetarStationReader;use Nemundo\Web\Site\AbstractJsonSite;class MetarStationJsonSite extends AbstractJsonSite{    /**     * @var MetarStationJsonSite     */    public static $site;    protected function loadSite()    {        //$this->title = 'Metar Station Kml';        $this->url = 'metar-station-json';        MetarStationJsonSite::$site = $this;    }    protected function loadJson()    {        $list = [];        $metarReader = new MetarStationReader();        $metarReader->limit=10;        foreach ($metarReader->getData() as $metarRow) {            $data=[];            $data['Station']=$metarRow->stationCode;            $list[]= $data;            //$this->addJsonRow($data);        }        $row['StationInfo']= $list;        $this->addJsonRow($row);    }}