<?phpnamespace Nemundo\Meteo\Base\Collection;use Nemundo\App\Application\Type\Collection\AbstractApplicationCollection;use Nemundo\Meteo\AviationWeather\Application\AviationWeatherApplication;use Nemundo\Meteo\Emagramm\Application\EmagrammApplication;use Nemundo\Meteo\Isd\Application\IsdApplication;use Nemundo\Meteo\Meteocentrale\Application\MeteocentraleApplication;class MeteoApplicationCollection extends AbstractApplicationCollection{    protected function loadCollection()    {        $this            ->addApplication(new AviationWeatherApplication())            ->addApplication(new IsdApplication())            ->addApplication(new MeteocentraleApplication())            ->addApplication(new EmagrammApplication());    }}