<?phpnamespace Nemundo\Meteo\Base\Com;use Nemundo\Html\Container\AbstractHtmlContainer;class WindArrow extends AbstractHtmlContainer{    /**     * @var int     */    public $degree;    public function getContent()    {        $this->tagName = 'div';        $this->addContent('&#8595;');        $this->addAttribute('style', 'width: 20px; height: 20px;transform: rotate(' . $this->degree . 'deg)');        return parent::getContent();    }}