<?phpnamespace Nemundo\Meteo\Base\Download;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\File;use Nemundo\Core\WebRequest\WebRequest;use Nemundo\Project\Path\TmpPath;class FileDownload extends AbstractBase{    /**     * @var string     */    public $url;    /**     * @var string     */    public $filename;    /**     * @var bool     */    public $deleteFile = false;    public function downloadFile()    {        $filename = (new TmpPath())            ->addPath('meteo')            ->createPath()            ->addPath($this->filename)            ->getFilename();        $file = new File($filename);        if ($file->fileNotExists()) {            (new WebRequest())->downloadUrl($this->url, $filename);        }        if ($this->deleteFile) {            (new File($filename))->deleteFile();        }        return $filename;    }}