<?phpnamespace Nemundo\Meteo\Base\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\Meteo\Dwd\Application\DwdApplication;use Nemundo\Meteo\Emagramm\Application\EmagrammApplication;use Nemundo\Meteo\Eumetsat\Application\EumetsatApplication;use Nemundo\Meteo\Isd\Application\IsdApplication;use Nemundo\Meteo\Meteocentrale\Application\MeteocentraleApplication;use Nemundo\Meteo\MeteoProject;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Application\MeteoschweizAllgemeineLageApplication;use Nemundo\Meteo\MeteoschweizBlog\Application\MeteoschweizBlogApplication;use Nemundo\Meteo\Sat24\Application\Sat24Application;use Nemundo\Meteo\Zamg\Application\ZamgApplication;use Nemundo\App\Application\Type\Install\AbstractInstall;class MeteoApplicationInstall extends AbstractInstall{    public function install()    {        (new ApplicationSetup())            ->addApplication(new IsdApplication())            ->addApplication(new DwdApplication())            ->addApplication(new ZamgApplication())            ->addApplication(new Sat24Application())            ->addApplication(new EumetsatApplication())            ->addApplication(new EmagrammApplication())            ->addApplication(new MeteoschweizAllgemeineLageApplication())            ->addApplication(new MeteoschweizBlogApplication())            ->addApplication(new MeteocentraleApplication());    }}