<?phpnamespace Paranautik\Physik;use Nemundo\Core\Base\AbstractBase;class Humidity extends AbstractBase{    //public $temperature;    //public $pressure;    //public $relativeHumidity;    // https://rechneronline.de/barometer/luftfeuchtigkeit.php    // https://carnotcycle.wordpress.com/2012/08/04/how-to-convert-relative-humidity-to-absolute-humidity/    public function getAbsoluteHumidity($temperature, $relativeHumidity)    {        //$gasKonstante =   461.52;        //$absolut = ($gasKonstante*($this->temperature+273.1));        $absolut = round((6.112 * pow(2.71828, ((17.67 * $temperature) / ($temperature + 243.5))) * $relativeHumidity * 2.1674) / (273.15 + $temperature), 2);        return $absolut;    }}