<?phpnamespace Nemundo\Meteo\Dwd\Com\ListBox;use Nemundo\Meteo\Dwd\Data\Map\MapReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class MapListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Map';        $reader = new MapReader();        foreach ($reader->getData() as $mapRow) {            $this->addItem($mapRow->id, $mapRow->map);        }        return parent::getContent();    }}