<?phpnamespace Nemundo\Meteo\Dwd\Content\LatestMap;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Meteo\Dwd\Com\ListBox\MapListBox;class LatestMapContentForm extends AbstractContentForm{    /**     * @var LatestMapContentType     */    public $contentType;    /**     * @var MapListBox     */    private $map;    public function getContent()    {        $this->map=new MapListBox($this);        return parent::getContent();    }    public function onSubmit()    {        $this->contentType->mapId=$this->map->getValue();        $this->contentType->saveType();    }}