<?phpnamespace Nemundo\Meteo\Dwd\Content\LatestMap;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Meteo\Dwd\Data\MapContent\MapContent;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentReader;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentRow;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentUpdate;class LatestMapContentType extends AbstractContentType{        public $mapId;        protected function loadContentType()    {        $this->typeLabel = 'DWD - Latest Map';        $this->typeId = '1e06f948-9c8c-47d0-bc19-73145ab694b7';        $this->formClassList[] = LatestMapContentForm::class;        $this->viewClassList[]  = LatestMapContentView::class;    }    protected function onCreate()    {                $data=new MapContent();        $data->mapId=$this->mapId;        $this->dataId=$data->save();            }    protected function onUpdate()    {        $update=new MapContentUpdate();        $update->mapId=$this->mapId;        $update->updateById($this->dataId);            }    protected function onDataRow()    {        $reader=new MapContentReader();        $reader->model->loadMap();        $this->dataRow=$reader->getRowById($this->dataId);    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|MapContentRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        $subject=$this->getDataRow()->map->map;        return $subject;    }}