<?phpnamespace Nemundo\Meteo\Dwd\Content\LatestMap;use Nemundo\Content\View\AbstractContentView;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Meteo\Dwd\Data\MapImage\MapImageReader;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class LatestMapContentView extends AbstractContentView{    /**     * @var LatestMapContentType     */    public $contentType;    public function getContent()    {        $reader = new MapImageReader();        $reader->filter->andEqual($reader->model->mapId,$this->contentType->getDataRow()->mapId);        $reader->addOrder($reader->model->id,SortOrder::DESCENDING);        $reader->limit=1;        foreach ($reader->getData() as $imageRow) {            $img=new BootstrapResponsiveImage($this);            $img->src=$imageRow->image->getUrl();// getImageUrl($reader->model->image);  //->imageAutoSize400);        }        return parent::getContent();    }}