<?phpnamespace Nemundo\Meteo\Dwd\Content\MapSlider;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Meteo\Dwd\Com\ListBox\MapListBox;class MapSliderContentForm extends AbstractContentForm{    /**     * @var MapSliderContentType     */    public $contentType;    /**     * @var MapListBox     */    private $map;    public function getContent()    {        $this->map = new MapListBox($this);        return parent::getContent();    }    public function onSubmit()    {        $this->contentType->mapId = $this->map->getValue();        $this->contentType->saveType();    }}